    "5efc3cb6355c6647041b6e49",
    "59009221c102763beda7cf04",
]});
const developersPermission = new codefresh.Permission("developersPermission", {
    team: developersTeam.id,
    resource: "pipeline",
    action: "run",
    tags: [
        "demo",
        "test",
    ],
});
```
