                .priority(5)
                .specTemplates(PipelineSpecSpecTemplateArgs.builder()
                    .repo("codefresh-contrib/react-sample-app")
                    .path("./codefresh.yml")
                    .revision("master")
                    .context("git")
                    .build())
                .contexts(                
                    "context1-name",
                    "context2-name")
                .triggers(                
                    PipelineSpecTriggerArgs.builder()
                        .branchRegex("/.*/gi")
                        .context("git")
                        .description("Trigger for commits")
                        .disabled(false)
                        .events("push.heads")
                        .modifiedFilesGlob("")
                        .name("commits")
                        .provider("github")
                        .repo("codefresh-contrib/react-sample-app")
                        .type("git")
                        .build(),
                    PipelineSpecTriggerArgs.builder()
                        .branchRegex("/.*/gi")
                        .context("git")
                        .description("Trigger for tags")
                        .disabled(false)
                        .events("push.tags")
                        .modifiedFilesGlob("")
                        .commitStatusTitle("tags-trigger")
                        .name("tags")
                        .provider("github")
                        .repo("codefresh-contrib/react-sample-app")
                        .type("git")
                        .build())
                .variables(Map.ofEntries(
                    Map.entry("MY_PIP_VAR", "value"),
                    Map.entry("ANOTHER_PIP_VAR", "another_value")
                ))
                .build())
            .build());

    }
}
